import React, { useState } from 'react';

const ConfirmationReserve = () => {
  const [parkingOption, setParkingOption] = useState('Parking1');
  const [hours, setHours] = useState(1);

  const handleParkingChange = (e) => {
    setParkingOption(e.target.value);
  };

  const handleHoursChange = (e) => {
    setHours(e.target.value);
  };

  const handleConfirmReservation = () => {
    // Aquí puedes enviar la reserva al servidor o realizar alguna acción
    console.log('Reserva confirmada:', { parkingOption, hours });
  };

  return (
    <div className="container" style={{ width: '50%' }}>
      <h1>Confirmar Reserva</h1>
      <form>
        <div className="mb-3">
          <label htmlFor="parkingOption" className="form-label">
            Parking
          </label>
          <select
            className="form-select"
            id="parkingOption"
            name="parkingOption"
            value={parkingOption}
            onChange={handleParkingChange}
          >
            <option value="Parking1">Parking 1</option>
            <option value="Parking2">Parking 2</option>
            <option value="Parking3">Parking 3</option>
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="hours" className="form-label">
            Cantidad de Horas
          </label>
          <input
            type="number"
            className="form-control"
            id="hours"
            name="hours"
            value={hours}
            onChange={handleHoursChange}
          />
        </div>
        <button
          type="button"
          className="btn btn-primary"
          onClick={handleConfirmReservation}
        >
          Confirmar Reserva
        </button>
      </form>
    </div>
  );
};

export default ConfirmationReserve;
