import React from 'react';
import logo from '../images/logoname.png';
import { Link } from 'react-router-dom';
import { UserContext } from "../context/UserContext";
import { useContext } from "react";

export const Navbar = () => {
    const { user, setUser } = useContext(UserContext);

    const handleLogOut = () => {
        setUser(false)
        console.log('salir')
    }

    // Define las listas de enlaces para cada rol
    const adminLinks = (
        <ul className="navbar-nav w-100 justify-content-between align-items-end">
            <li className="nav-item rounded mx-auto d-block sm-2">
                <Link className="nav-link active" aria-current="page" to="/reservas"><h5>Reservas</h5></Link>
            </li>
            {/* Agrega más enlaces para el rol de administrador aquí */}
        </ul>
    );

    const userLinks = (
        <ul className="navbar-nav w-100 justify-content-between align-items-end">
            <li className="nav-item rounded mx-auto d-block sm-2">
                <Link className="nav-link active" aria-current="page" to="/reservas"><h5>Reservas</h5></Link>
            </li>
            {/* Agrega más enlaces para el rol de usuario aquí */}
        </ul>
    );

    const guestLinks = (
        <li className="nav-item mx-auto d-block">
            <Link className="btn text-white" style={{ backgroundColor: '#F5B041', marginRight: "5px", marginBottom: "5px" }} to="/login">Ingresar</Link>
            <Link className="btn text-white" style={{ backgroundColor: '#F5B041', marginBottom: "5px" }} to="/register">Registrarme</Link>
        </li>
    );

    // Selecciona las listas de enlaces basadas en el rol del usuario
    const linksToRender = user.role === '1' ? adminLinks : user.role === '2' ? userLinks : guestLinks;

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark" style={{ backgroundColor: '#4C4F62' }}>
                <div className="container-fluid">
                    <Link className="navbar-brand" to='/home'>
                        <img src={logo} width="250px" height="110px" alt="Logo y nombre" to="/home" />
                    </Link>

                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
                        aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        {linksToRender}
                    </div>
                </div>
            </nav>
        </div>
    )
}

export default Navbar;
