import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export const UserEdit = () => {
    const { userId } = useParams();
    const [userData, setUserData] = useState({
        name: '',
        email: '',
        phone: '',
        password: '',
        role: '',
    });
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/userlist/${userId}`);
                const data = response.data;
                setUserData(data);
            } catch (error) {
                console.error('Error al obtener los datos del usuario', error);
            }
        };

        fetchUserData();
    }, [userId]);

    const handleEditUser = async () => {
        try {
            // Realiza una solicitud para actualizar los datos del usuario en el servidor
            await axios.put(`http://localhost:5000/userlist/${userId}`, userData);
            console.log('Usuario actualizado con éxito');
            setIsEditing(false);
        } catch (error) {
            console.error('Error al actualizar el usuario', error);
        }
    };

    const handleInputChange = (e) => {
        // Maneja los cambios en los campos de entrada y actualiza el estado userData
        const { name, value } = e.target;
        setUserData({ ...userData, [name]: value });
    };

    return (
        <div>
            <h1>Editar Usuario</h1>
            <form>
                {Object.keys(userData).map((key) => (
                    <div key={key} className="mb-3">
                        <label htmlFor={key} className="form-label">
                            {key.charAt(0).toUpperCase() + key.slice(1)}
                        </label>
                        {isEditing ? (
                            <input
                                type={key === 'password' ? 'password' : 'text'}
                                className="form-control"
                                id={key}
                                name={key}
                                value={userData[key]}
                                onChange={handleInputChange}
                            />
                        ) : (
                            <span>{userData[key]}</span>
                        )}
                    </div>
                ))}
                {isEditing ? (
                    <button type="button" className="btn btn-primary" onClick={handleEditUser}>
                        Guardar Cambios
                    </button>
                ) : (
                    <button type="button" className="btn btn-secondary" onClick={() => setIsEditing(true)}>
                        Editar
                    </button>
                )}
            </form>
        </div>
    );
};

export default UserEdit;
