import React from 'react';
import logo from '../images/logoname.png';
import { Link } from 'react-router-dom';
import { UserContext } from "../context/UserContext";
import { useContext } from "react";

export const Navbar = () => {
    const { user, setUser } = useContext(UserContext);

    const handleLogOut = () => {
        setUser(false);
        console.log('salir');
    }

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark" style={{ backgroundColor: '#4C4F62' }}>
                <div className="container-fluid">
                    <Link className="navbar-brand" to='/home'>
                        <img src={logo} width="250px" height="110px" alt="Logo y nombre" to="/home" />
                    </Link>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
                        aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav w-100 justify-content-between align-items-end">
                            {user && user.role === '1' ? (
                                <li className="nav-item mx-auto d-block">
                                    <button
                                        className="btn nav-link active"
                                        onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                    >
                                        Panel de Administración
                                    </button>
                                    {isDropdownOpen && (
                                        <ul className="dropdown-menu">
                                            <li>
                                                <Link to='/estacionamientos' className="dropdown-item">
                                                    Lista de Estacionamientos
                                                </Link>
                                            </li>
                                            <li>
                                                <Link to='/usuarios' className="dropdown-item">
                                                    Lista de Usuarios
                                                </Link>
                                            </li>
                                        </ul>
                                    )}
                                </li>
                            ) : null}
                            {user ? (
                                <li className="nav-item mx-auto d-block">
                                    <button className="btn text-white" style={{ backgroundColor: '#F5B041' }} onClick={handleLogout}>
                                        Cerrar Sesión
                                    </button>
                                </li>
                            ) : (
                                <li className="nav-item mx-auto d-block">
                                    {/* ... Botones para inicio de sesión y registro ... */}
                                </li>
                            )}
                        </ul>
                    </div>
                </div>
            </nav>
        </div>
    );
};


export default Navbar;
