import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export const UserEdit = () => {
    const { userId } = useParams();
    const [userData, setUserData] = useState({
        name: '',
        email: '',
        phone: '',
        password: '', // Agregamos el campo contraseña
        role: '',
    });

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/userlist/${userId}`);
                const data = response.data;
                setUserData(data);
            } catch (error) {
                console.error('Error al obtener los datos del usuario', error);
            }
        };

        fetchUserData();
    }, [userId]);

    const handleEditUser = async () => {
        try {
            // Realiza una solicitud para actualizar los datos del usuario en el servidor
            await axios.put(`http://localhost:5000/userlist/${userId}`, userData);
            console.log('Usuario actualizado con éxito');
            // Puedes redirigir al usuario a la página de detalles del usuario o a otra página
        } catch (error) {
            console.error('Error al actualizar el usuario', error);
        }
    };

    const handleInputChange = (e) => {
        // Maneja los cambios en los campos de entrada y actualiza el estado userData
        const { name, value } = e.target;
        setUserData({ ...userData, [name]: value });
    };

    return (
        <div>
            <h1>Editar Usuario</h1>
            <form>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">
                        Nombre y Apellido
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        name="name"
                        value={userData.name}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">
                        Correo Electrónico
                    </label>
                    <input
                        type="email"
                        className="form-control"
                        id="email"
                        name="email"
                        value={userData.email}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="phone" className="form-label">
                        Teléfono
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="phone"
                        name="phone"
                        value={userData.phone}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">
                        Contraseña
                    </label>
                    <input
                        type="password"
                        className="form-control"
                        id="password"
                        name="password"
                        value={userData.password}
                        onChange={handleInputChange}
                    />
                </div>
                <button type="button" className="btn btn-primary" onClick={handleEditUser}>
                    Guardar Cambios
                </button>
            </form>
        </div>
    );
};

export default UserEdit;
