import React, { useState } from 'react';
import Swal from 'sweetalert2';

const ConfirmationReserve = () => {
  const [parkingOption, setParkingOption] = useState('Parking1');
  const [hours, setHours] = useState(1);

  // Define los precios por hora, turno, mediodía y día completo
  const prices = {
    Parking1: {
      hour: 5, // Precio por hora
      shift: 20, // Precio por turno
      noon: 35, // Precio por mediodía
      fullDay: 50, // Precio por día completo
    },
    Parking2: {
      hour: 6,
      shift: 25,
      noon: 40,
      fullDay: 60,
    },
    Parking3: {
      hour: 4,
      shift: 18,
      noon: 30,
      fullDay: 45,
    },
  };

  const [totalPrice, setTotalPrice] = useState(prices.Parking1.hour); // Precio inicial por hora

  const handleParkingChange = (e) => {
    const selectedParkingOption = e.target.value;
    setParkingOption(selectedParkingOption);

    // Actualiza el precio al cambiar la opción de estacionamiento
    setTotalPrice(prices[selectedParkingOption].hour * hours);
  };

  const handleHoursChange = (e) => {
    const selectedHours = parseInt(e.target.value);
    setHours(selectedHours);

    // Actualiza el precio al cambiar la cantidad de horas
    setTotalPrice(prices[parkingOption].hour * selectedHours);
  };

  const handleConfirmReservation = () => {
    // Aquí puedes enviar la reserva al servidor o realizar alguna acción

    Swal.fire('Reserva realizada con éxito', '', 'success');
  };

  return (
    <div className="container" style={{ width: '50%', marginTop: '50px' }}>
      <div style={{ textAlign: 'center' }}>
          <h1>Confirmación de Reserva</h1>
          <div style={{ width: '100%', backgroundColor: '#F5B041', height: '5px', margin: 'auto', marginBottom: '20px' }}></div>
      </div>
      <form>
        <div className="mb-3" style={{ marginTop: '50px' }}>
          <label htmlFor="parkingOption" className="form-label">
            <h4>Parking</h4>
          </label>
          <select
            className="form-select"
            id="parkingOption"
            name="parkingOption"
            value={parkingOption}
            onChange={handleParkingChange}
          >
            <option value="Parking1">Parking San Martín</option>
            <option value="Parking2">Parking Francia</option>
            <option value="Parking3">Parking Plaza España</option>
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="hours" className="form-label">
            <h4>Cantidad de Horas</h4>
          </label>
          <input
            type="number"
            className="form-control"
            id="hours"
            name="hours"
            value={hours}
            onChange={handleHoursChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="totalPrice" className="form-label">
            <h4>Precio Total</h4>
          </label>
          <input
            type="text"
            className="form-control"
            id="totalPrice"
            name="totalPrice"
            value={`$${totalPrice}`} // Muestra el precio total
            readOnly
          />
        </div>
        <div style={{ textAlign: 'center' }}>
          <button
            type="button"
            className="btn btn-warning text-white"
            onClick={handleConfirmReservation}
          >
            Confirmar Reserva
          </button>
        </div>
      </form>
    </div>
  );
};

export default ConfirmationReserve;
