import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const containerStyle = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  height: '100vh',
};

const formContainerStyle = {
  width: '50%', // Ajusta el ancho del formulario principal
  margin: '0 auto', // Centra el formulario horizontalmente
};

const formStyle = {
  padding: '20px',
  border: '1px solid #ccc',
  borderRadius: '5px',
};

const sideFormStyle = {
  width: '50%', // Ajusta el ancho del formulario lateral
};

export const UserEdit = () => {
  const { userId } = useParams();
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    role: '',
  });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/userlist/${userId}`);
        const data = response.data;
        setUserData(data);
      } catch (error) {
        console.error('Error al obtener los datos del usuario', error);
      }
    };

    fetchUserData();
  }, [userId]);

  const handleEditUser = async () => {
    try {
      // Realiza una solicitud para actualizar los datos del usuario en el servidor
      await axios.put(`http://localhost:5000/userlist/${userId}`, userData);
      console.log('Usuario actualizado con éxito');
      // Puedes redirigir al usuario a la página de detalles del usuario o a otra página
    } catch (error) {
      console.error('Error al actualizar el usuario', error);
    }
  };

  const handleInputChange = (e) => {
    // Maneja los cambios en los campos de entrada y actualiza el estado userData
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  return (
    <div style={containerStyle}>
      <div style={formContainerStyle}>
        <div style={formStyle}>
          <h1>Editar Usuario</h1>
          <form>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                Nombre y Apellido
              </label>
              <input
                type="text"
                className="form-control"
                id="name"
                name="name"
                value={userData.name}
                onChange={handleInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">
                Correo Electrónico
              </label>
              <input
                type="email"
                className="form-control"
                id="email"
                name="email"
                value={userData.email}
                onChange={handleInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="phone" className="form-label">
                Teléfono
              </label>
              <input
                type="text"
                className="form-control"
                id="phone"
                name="phone"
                value={userData.phone}
                onChange={handleInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">
                Contraseña
              </label>
              <input
                type="password"
                className="form-control"
                id="password"
                name="password"
                value={userData.password}
                onChange={handleInputChange}
              />
            </div>

            <button type="button" className="btn btn-primary" onClick={handleEditUser}>
              Guardar Cambios
            </button>
          </form>
        </div>

        {/* Formulario con los datos actuales */}
        <div style={sideFormStyle}>
          <div style={formStyle}>
            <h2>Datos Actuales</h2>
            <form>
              <div className="mb-3">
                <label htmlFor="currentName" className="form-label">
                  Nombre y Apellido
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="currentName"
                  name="currentName"
                  value={userData.name}
                  disabled
                />
              </div>
              <div className="mb-3">
                <label htmlFor="currentEmail" className="form-label">
                  Correo Electrónico
                </label>
                <input
                  type="email"
                  className="form-control"
                  id="currentEmail"
                  name="currentEmail"
                  value={userData.email}
                  disabled
                />
              </div>
              <div className="mb-3">
                <label htmlFor="currentPhone" className="form-label">
                  Teléfono
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="currentPhone"
                  name="currentPhone"
                  value={userData.phone}
                  disabled
                />
              </div>
              <div className="mb-3">
                <label htmlFor="currentPassword" className="form-label">
                  Contraseña
                </label>
                <input
                  type="password"
                  className="form-control"
                  id="currentPassword"
                  name="currentPassword"
                  value={userData.password}
                  disabled
                />
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserEdit;
