import React, { useState } from 'react';
import axios from 'axios';
import { Field, Form, Formik } from 'formik';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';

export const Register = () => {
    const navigate = useNavigate();

    const [isRegistered, setIsRegistered] = useState(false); // Estado para controlar la visibilidad de los componentes

    const initialValues = {
        name: '',
        email: '',
        password: '',
        phone: '',
    };

    const handleRegister = async (values) => {
        try {
            const response = await axios.post('http://localhost:5000/auth/register', values);
            console.log(response.data);
            Swal.fire({
                position: 'top-end',
                icon: 'success',
                title: 'Registro exitoso',
                showConfirmButton: false,
                timer: 1800,
            });
            setIsRegistered(true); // Cambiar el estado para ocultar los componentes después del registro
            navigate('/dashboard');
        } catch (error) {
            if (error.response && error.response.status === 400) {
                // 409 status code indicates conflicto, meaning the email is already registered
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    text: 'El correo electrónico ya está registrado',
                });
            } else {
                console.log(error);
            }
        }
    };

    return (
        <div>
            <br />
            <div className="row">
                <div className="col-md-4 col-sm-1"></div>
                <div className={`col-md-4 col-sm-1 border border-1 ${isRegistered ? 'd-none' : ''}`}>
                    <div style={{ padding: "30px" }}>
                        <h2>Registrarse</h2>
                        <Formik initialValues={initialValues} onSubmit={handleRegister}>
                            <Form>
                                {/* ... Componentes de registro aquí */}
                            </Form>
                        </Formik>
                    </div>
                </div>
                <div className={`col-md-4 col-sm-1 ${isRegistered ? 'd-none' : ''}`}></div>
            </div>
        </div>
    )
}

export default Register;
