import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../context/UserContext';
import Swal from 'sweetalert2';


const containerStyle = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  height: '100vh',
};

const formContainerStyle = {
  width: '50%',
  display: 'flex',
};

const formStyle = {
  padding: '20px',
  border: '1px solid #ccc',
  borderRadius: '5px',
  flex: 1,
};

export const UserEdit = () => {
  const { user: contextUser } = useContext(UserContext);
  const [userData, setUserData] = useState([]);

  const [editedUser, setEditedUser] = useState({ id: 0, name: '', phone: '', mail: '', password: '' });

  const navigate = useNavigate();

  useEffect(() => {
    fetchUser();
  }, []);

  const fetchUser = async () => {
    try {
      const response = await axios.get('http://localhost:5000/userlist');
      console.log(response);
      setUserData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const onEditUser = (user) => {
    setEditedUser(user);
  };

  const handleInputChange = (e) => {
    console.log('valor de e.target.name', e.target.name);
    console.log('valor de e.target.value', e.target.value);
    setEditedUser({ ...editedUser, [e.target.name]: e.target.value });
  };

  const handleUpdateUser = async () => {
    try {
      await axios.put(`http://localhost:5000/user/${editedUser.id}`, editedUser);
      fetchUser();
      setEditedUser({ id: 0, name: '', phone: '', mail: '', password: '' });
      Swal.fire({
        icon: 'success',
        title: 'Editado correctamente',
        showConfirmButton: false,
        timer: 1800,
      });
      navigate('/dashboard');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div style={containerStyle}>
      <div style={formContainerStyle}>
        <div style={formStyle}>
          <h1>Editar Usuario</h1>
          <div className="mb-3">
            <input
              type="text"
              name="name"
              value={editedUser.name}
              onChange={handleInputChange}
              placeholder="Nombre"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Correo Electr√≥nico
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={editedUser.email || ''}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="phone" className="form-label">
              Tel√©fono
            </label>
            <input
              type="text"
              className="form-control"
              id="phone"
              name="phone"
              value={userData.phone || ''}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Contrase√±a
            </label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={userData.password || ''}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserEdit;
