import React, { useEffect, useState } from 'react';
import axios from 'axios';

const tableContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    width: '80%',
    margin: '0 auto',
};

const searchContainerStyle = {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100%',
    marginBottom: '20px',
};

const searchInputStyle = {
    flex: 1, // Hace que el campo de búsqueda ocupe el espacio restante
    marginRight: '10px', // Añade un margen a la derecha del campo de búsqueda
};

const tableStyle = {
    border: '1px solid #ccc',
    borderCollapse: 'collapse',
    width: '100%',
};

const thStyle = {
    background: '#f2f2f2',
    border: '1px solid #ccc',
    padding: '8px',
    textAlign: 'left',
};

const tdStyle = {
    border: '1px solid #ccc',
    padding: '8px',
    textAlign: 'left',
};

export const Users = () => {
    const [userData, setUserData] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get('http://localhost:5000/users'); // Cambia la URL a la que corresponda tu API de usuarios
                const data = response.data;
                setUserData(data);
            } catch (error) {
                console.error('Error al obtener los datos de usuarios', error);
            }
        };

        fetchUserData();
    }, []);

    // Puedes reemplazar esta función con una función para eliminar usuarios si lo deseas
    const handleDeleteUser = async (id) => {
        try {
            // Realiza una solicitud DELETE para eliminar al usuario con el ID especificado
            // Actualiza la lista de usuarios después de la eliminación
        } catch (error) {
            console.error('Error al eliminar el usuario', error);
        }
    };

    // Filtra la lista de usuarios según el término de búsqueda
    const filteredUserData = userData.filter((user) =>
        user.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div style={tableContainerStyle}>
            <h1>Lista de Usuarios</h1>

            <div style={searchContainerStyle}>
                {/* Campo de búsqueda */}
                <input
                    type="text"
                    placeholder="Buscar por nombre de usuario"
                    className="form-control"
                    style={searchInputStyle} // Aplica el estilo al campo de búsqueda
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />

                {/* Botón para borrar el campo de búsqueda */}
                <button
                    className="btn btn-secondary"
                    onClick={() => setSearchTerm('')}
                >
                    Borrar
                </button>
            </div>

            <table style={tableStyle}>
                <thead>
                    <tr>
                        <th style={thStyle}>ID</th>
                        <th style={thStyle}>Nombre</th>
                        <th style={thStyle}>Email</th>
                        <th style={thStyle}>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredUserData.map((user) => (
                        <tr key={user.id}>
                            <td style={tdStyle}>{user.id}</td>
                            <td style={tdStyle}>{user.name}</td>
                            <td style={tdStyle}>{user.email}</td>
                            <td style={tdStyle}>
                                <button
                                    className="btn btn-danger"
                                    onClick={() => handleDeleteUser(user.id)}
                                >
                                    Eliminar
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};
