import React, { useState } from 'react';

const Reserves = () => {
    const [selectedParking, setSelectedParking] = useState('');
    const [selectedTime, setSelectedTime] = useState(1);
    const [selectedTurn, setSelectedTurn] = useState('8h');

    const handleParkingChange = (e) => {
        setSelectedParking(e.target.value);
    };

    const handleTimeChange = (e) => {
        setSelectedTime(parseInt(e.target.value));
    };

    const handleTurnChange = (e) => {
        setSelectedTurn(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí puedes enviar los datos de la reserva al servidor o realizar otras acciones.
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <h1>Realizar Reserva</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="parking">Seleccionar Estacionamiento:</label>
                    <select id="parking" value={selectedParking} onChange={handleParkingChange}>
                        <option value="">Seleccionar Estacionamiento</option>
                        <option value="Parking San Martin">Parking San Martin</option>
                        <option value="Parking Francia">Parking Francia</option>
                        <option value="Parking Plaza España">Parking Plaza España</option>
                    </select>
                </div>
                <div>
                    <label htmlFor="time">Cantidad de Horas:</label>
                    <input
                        type="number"
                        id="time"
                        min="1"
                        max="4"
                        value={selectedTime}
                        onChange={handleTimeChange}
                    />
                </div>
                <div>
                    <label>Seleccionar Turno:</label>
                    <div>
                        <input
                            type="radio"
                            id="turn8h"
                            name="turno"
                            value="8h"
                            checked={selectedTurn === '8h'}
                            onChange={handleTurnChange}
                        />
                        <label htmlFor="turn8h">8 Horas</label>
                    </div>
                    <div>
                        <input
                            type="radio"
                            id="turn24h"
                            name="turno"
                            value="24h"
                            checked={selectedTurn === '24h'}
                            onChange={handleTurnChange}
                        />
                        <label htmlFor="turn24h">24 Horas</label>
                    </div>
                </div>
                <button type="submit">Realizar Reserva</button>
            </form>
        </div>
    );
};

export default Reserves;
