import React, { useState } from 'react';
import axios from 'axios';
import { Field, Form, Formik } from 'formik';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';

export const ParkingForm = () => {
    const navigate = useNavigate();
    const [idToDelete, setIdToDelete] = useState('');

    const initialValues = {
        availabity: true,
        parking: ''
    };

    const handleCreateParking = async (values) => {
        // ... (código para crear una plaza de estacionamiento)

        try {
            // Realizar la solicitud POST para crear una plaza de estacionamiento
        } catch (error) {
            // Manejar errores si la solicitud POST falla
        }
    };

    const handleDeleteParking = async (id) => {
        try {
            // Realizar la solicitud DELETE al servidor para eliminar una plaza de estacionamiento
            await axios.delete(`http://localhost:5000/parkingform/${id}`);
            Swal.fire({
                icon: 'success',
                title: 'Plaza de estacionamiento eliminada con éxito',
                showConfirmButton: false,
                timer: 1800,
            });
            // Luego, puedes redirigir al usuario a la página de ofertas u otro lugar
            navigate('/offers');
        } catch (error) {
            console.log(error);
            Swal.fire({
                icon: 'error',
                title: 'Error al eliminar la plaza de estacionamiento',
                text: 'Por favor, inténtalo de nuevo más tarde',
            });
        }
    };

    return (
        <div>
            <br />
            <div className="row">
                <div className="col-md-4 col-sm-1"></div>
                <div className="col-md-4 col-sm-1 border border-1">
                    <div style={{ padding: "30px" }}>
                        <h2>Crear Plaza de Estacionamiento</h2>
                        <Formik initialValues={initialValues} onSubmit={handleCreateParking}>
                            <Form>
                                <div className="mb-3">
                                    <label htmlFor="parking" className="form-label">Elija el parking</label>
                                    <Field as="select" className="form-select" id="parking" name="parking">
                                        <option value="">Seleccionar Parking</option>
                                        <option value="san_martin">San Martín</option>
                                        <option value="francia">Francia</option>
                                        <option value="plaza_esp">Plaza España</option>
                                    </Field>
                                </div>
                                <div className="d-grid gap-2">
                                    <button type="submit" className="btn m-2 text-white" style={{ backgroundColor: '#F5B041' }}>Crear Plaza</button>
                                </div>
                            </Form>
                        </Formik>
                        <button
                            onClick={() => handleDeleteParking(idToDelete)}
                            className="btn m-2 text-white"
                            style={{ backgroundColor: 'red' }}
                        >
                            Eliminar Plaza
                        </button>
                    </div>
                </div>
                <div className="col-md-4 col-sm-1"></div>
            </div>
        </div>
    );
};



export default ParkingForm;
