import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from '../context/UserContext';
import logo from '../imagenes/um.png';

export const Navbar = () => {
  const { user, setUser } = useContext(UserContext);

  const handleLogOut = () => {
    setUser(false);
    console.log('salir');
  };

  const userOptions = {
    admin: [
      { label: 'Dashboard', path: '/dashboard' },
      { label: 'Usuarios registrados', path: '/userList' },
      { label: 'Ofertas creadas', path: '/offers' },
      { label: 'Salir', onClick: handleLogOut },
    ],
    user: [
      { label: 'Ofertas', path: '/offers' },
      { label: 'Mis postulaciones', path: '/myApplications' },
      { label: 'Salir', onClick: handleLogOut },
    ],
    guest: [
      { label: 'Home', path: '/home' },
      { label: 'Login', path: '/login' },
      { label: 'Registro', path: '/register' },
    ],
  };

  const renderNavLinks = (options) => {
    return options.map((option, index) => (
      <li key={index} className="nav-item">
        {option.onClick ? (
          <Link className="nav-link" to="#" onClick={option.onClick}>
            {option.label}
          </Link>
        ) : (
          <Link className="nav-link" to={option.path}>
            {option.label}
          </Link>
        )}
      </li>
    ));
  };

  return (
    <div className="header_section">
      <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light" data-bs-theme="dark">
          <a className="navbar-brand" href="/">
            <img src={logo} alt="" width="70px" height="70px" />
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            {renderNavLinks(userOptions[user.role || 'guest'])}
          </div>
        </nav>
      </div>
    </div>
  );
};
