import React from 'react';
import { Form, Formik, Field } from 'formik';
import axios from 'axios';

export const Login = () => {
  const initialValues = {
    email: '',
    password: '',
  };

  const handleForm = async (values) => {
    console.log('values:', values);
    try {
      const response = await axios.post('http://localhost:5000/auth/login', values);
      console.log(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center align-items-center min-vh-100">
        <div className="col-md-4 col-sm-12">
          <div className="border border-1 p-4">
            <h2 className="text-center mb-4">Iniciar Sesi칩n</h2>
            <Formik initialValues={initialValues} onSubmit={handleForm}>
              <Form>
                <div className="mb-3">
                  <label htmlFor="floatingEmail" className="form-label">Correo Electr칩nico</label>
                  <Field
                    type="email"
                    className="form-control"
                    id="floatingEmail"
                    placeholder="name@example.com"
                    name="email"
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="floatingPassword" className="form-label">Contrase침a</label>
                  <Field
                    type="password"
                    className="form-control"
                    id="floatingPassword"
                    placeholder="password"
                    name="password"
                  />
                </div>
                <div className="d-grid gap-2">
                  <button className="btn btn-primary" type="submit">Iniciar Sesi칩n</button>
                </div>
              </Form>
            </Formik>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
